var orgtext=new Array();var hasChanged=0;new Image().src='/community/graphics/stars1.gif';new Image().src='/community/graphics/stars2.gif';new Image().src='/community/graphics/stars3.gif';new Image().src='/community/graphics/stars4.gif';new Image().src='/community/graphics/stars5.gif';function change(id,starsid,newClass){if(!hasChanged[id])orgtext[id]=document.getElementById('rate-text'+id).innerHTML;hasChanged[id]=1;identity=document.getElementById(starsid);identity.className="rate"+newClass;document.getElementById('rate-text'+id).innerHTML=+newClass+' stars';}function changeback(id,starsid,newClass){identity=document.getElementById(starsid);identity.className="rate"+newClass;document.getElementById('rate-text'+id).innerHTML=orgtext[id]=="undefined"?"Rate":orgtext[id];}function ratethis(id,r){document.getElementById('ratevalue'+id).value=r;document.getElementById('rate-it'+id).submit();}